// Dependecies
import React from 'react';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';

import IconButton from '@material-ui/core/IconButton';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputAdornment from '@material-ui/core/InputAdornment';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';
import moment from 'moment';
import { ErrorMessage } from "@hookform/error-message";
import { useForm, Controller } from "react-hook-form";

// Components
import Card from '../components/Card';

function Password(props) {
  const [values, setValues] = React.useState({
    password: ''
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  //Form
  const {
    register,
    formState: { errors },
    handleSubmit
  } = useForm({
    criteriaMode: "all"
  });

  const onSubmit = (data) => console.log(data);

  
  return (
    <Container >
    <Grid container spacing={3}>
      <Grid item xs>
        <Grid>
            <div>
            <SearchIcon />
            </div>
            <InputBase placeholder="Search" />
        </Grid>

        <IconButton aria-label="add" className="">
          <AddIcon fontSize="small" />
        </IconButton>

        <Card account="Hola" user="Daniela" />

      </Grid>

      <Grid item xs={8}>
        <Paper >Formulario</Paper>
        <form onSubmit={handleSubmit(onSubmit)}>
      <h1>ErrorMessage</h1>
      <h4>Name</h4>
      <Controller
        render={({ field }) => <TextField {...field} />}
        name="firstName"
        control={control}
        defaultValue=""
        className="materialUIInput"
      />
          <input
            name="name"
            variant="outlined"
            maxRows={4}
            {...
              register(
              "name",
              { required: {
                  value: true, 
                  message: 'Campo obligatorio'
                  }, 
              maxLength: {
                  value: 50, 
                  message: 'No más de 50 carácteres!'
                  },
              minLength: {
                  value: 3, 
                  message: 'Mínimo 3 carácteres'
                  },
              pattern: {
                  value: /[a-zA-Z ]$/i,
                  message: "No se pueden ingresar caracteres."
              }})
            }
          />

      <input
        {...register("multipleErrorInput", {
          required: "This input is required.",
          pattern: {
            value: /\d+/,
            message: "This input is number only."
          },
          minLength: {
            value: 11,
            message: "This input must exceed 10 characters"
          }
        })}
      />
      <ErrorMessage
        errors={errors}
        name="multipleErrorInput"
        render={({ messages }) => {
          console.log("messages", messages);
          return messages
            ? Object.entries(messages).map(([type, message]) => (
                <p key={type}>{message}</p>
              ))
            : null;
        }}
      />

      <input type="submit" />
    </form>

        <form onSubmit={handleSubmit()}>
          
          {/* {errors.name && <p style={{color: "red"}}>{errors.name.message}</p>} */}

          <h4>Site URL</h4>
          <TextField 
          id="standard-basic" 
          variant="outlined"
          name="url"
          {...
            register(
            "url",
            { required: {
                value: true, 
                message: 'Campo obligatorio'
                }, 
            maxLength: {
                value: 50, 
                message: 'No más de 50 carácteres!'
                },
            minLength: {
                value: 3, 
                message: 'Mínimo 3 carácteres'
                },
            pattern: {
                value: /[a-zA-Z ]$/i,
                message: "No se pueden ingresar caracteres."
            }})
          }
          />

          <h4>Password</h4>
          <FormControl className="myForm" variant="outlined">
          <OutlinedInput
            id=""
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
          />
        </FormControl>

        <Button variant="outlined">Generate</Button>
        <Button variant="outlined">Copy</Button>

        <h4>Create At</h4>
          <TextField
            variant="outlined"
            maxRows={4}
            name="create_at"
            value={moment().format('YYYY-MM-DD h:mm:ss')}
            {...
              register(
              "FIELD_NAME",
              { required: {
                  value: true, 
                  message: 'Campo obligatorio'
                  }, 
              maxLength: {
                  value: 50, 
                  message: 'No más de 50 carácteres!'
                  },
              minLength: {
                  value: 3, 
                  message: 'Mínimo 3 carácteres'
                  },
              pattern: {
                  value: /[a-zA-Z ]$/i,
                  message: "No se pueden ingresar caracteres."
              }})
            }
          />

        {/* <Button size="medium" className="" variant="outlined" type="submit">
          Crear
        </Button> */}
        <input type="submit" />
        </form>

        <IconButton aria-label="delete" className="" variant="outlined">
          <DeleteIcon fontSize="small" />
        </IconButton>

        
      </Grid>

    </Grid>
  </Container>
  );
}
 
export default Password;
